def sol(m=8, n=8):
    def get_moves(r, c, visited):
        moves = []
        offsets = [(1, 2), (1, -2), (-1, 2), (-1, -2), (2, 1), (2, -1), (-2, 1), (-2, -1)]
        for dr, dc in offsets:
            nr, nc = r + dr, c + dc
            if 0 <= nr < m and 0 <= nc < n and (nr, nc) not in visited:
                moves.append((nr, nc))
        return moves

    def warnsdorff_heuristic(r, c, visited):
        # Sort possible next moves by the number of their own available next moves
        moves = get_moves(r, c, visited)

        def count_next_moves(pos):
            return len(get_moves(pos[0], pos[1], visited | {pos}))

        moves.sort(key=count_next_moves)
        return moves

    def backtrack(r, c, tour_path, visited_set):
        if len(tour_path) == m * n:
            return tour_path

        for nr, nc in warnsdorff_heuristic(r, c, visited_set):
            tour_path.append([nr, nc])
            visited_set.add((nr, nc))

